import React from "react";
import {Button, Col, Row, Upload, Form, Image} from "antd";
import {SearchOutlined, UploadOutlined} from "@ant-design/icons";
import QnATopNav from "../../function/QnATopNav";
import {useNavigate} from "react-router-dom";
import QnATopNav2 from "../../function/QnATopNav2";

function ImageSearch() {
    const navigate = useNavigate();
    const normFile = (e) => {
        console.log('Upload event:', e);
        if (Array.isArray(e)) {
            return e;
        }
        return e && e.fileList;
    };

    function onFinish(values) {
        console.log('Success:', values);
        navigate('/searchresult');
    }

    return (
        <div style={{backgroundColor: 'white', width: '100%', height: '100vh', overflow: 'hidden'}}>
            <div className="top-nav" style={{padding: '0 6%', fontSize: '16px'}}>
                <Row>
                    <Col span={23}>
                        길라잡이
                    </Col>
                </Row>
            </div>
            <QnATopNav2/>
            <div className="imageSearch" style={{
                display: 'flex', flexDirection: 'column',
                alignItems: 'center', width: '100%', height: '100vh', overflowX: 'hidden'
            }}>
                <div className="img" style={{marginTop: '20px'}}>
                    <Image
                        width={310}
                        height={310}
                        src="error"
                        fallback="data:image/png;base64,"
                    />
                </div>
                <Form className="padding" style={{marginTop: '24px'}}
                      onFinish={onFinish}>
                    <Form.Item
                        rules={[{required: true, message: '파일을 선택하세요!'}]}
                        name="upload"
                        label=""
                        valuePropName="fileList"
                        getValueFromEvent={normFile}
                    >
                        <Upload name="logo" listType="picture"
                                beforeUpload={() => false}>
                            <Button icon={<UploadOutlined/>}>사진 업로드</Button>
                        </Upload>
                    </Form.Item>
                    <Button type="primary" className="submitbtn" htmlType="submit" block
                            style={{marginTop: '-12px'}}>
                        <SearchOutlined/> 이미지 검색
                    </Button>
                </Form>
            </div>
        </div>
    )
}

export default ImageSearch;